var IMAGE_URL = 'data:image/jpeg;base64,'

var memo = null
module.exports = function isAutoOrienting() {
  if (memo) {
    return memo
  }
  return memo = new Promise(function (resolve, reject) {
    var imageElement = document.createElement('img')
    imageElement.style.position = 'absolute'
    imageElement.style.opacity = 0
    imageElement.onload = function () {
      var result = imageElement.height === 2 && imageElement.width === 1
      cleanup()
      resolve(result)
    }
    imageElement.onerror = fail
    imageElement.src = IMAGE_URL
    document.body.appendChild(imageElement)

    function fail(error) {
      memo = null
      cleanup()
      reject(error)
    }

    function cleanup() {
      document.body.removeChild(imageElement)
    }
  })
}
